function calcDowntime(keys){

    let hours = 0;
    let mins = 0;
    let secs = 0;
    if(keys.includes("hours")){
        hours = msg.payload[0].downtime.hours;

    }else hours = 0;
    if(keys.includes("minutes")){
        mins = msg.payload[0].downtime.minutes;
    }else mins = 0;
    if (keys.includes("seconds")){
        secs = msg.payload[0].downtime.seconds ;

    }else secs = 0;

    let totaldowntime = 60*60*hours + 60* mins +secs;

    return totaldowntime;



}

